const message = $input.item.json.message;
const apiKey = process.env.GEMINI_API_KEY;

const response = await fetch(
  `https://generativelanguage.googleapis.com/v1beta/models/text-embedding-004:embedContent?key=${apiKey}`,
  {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      content: {
        parts: [
          {
            text: message
          }
        ]
      }
    })
  }
);

const data = await response.json();

return {
  json: {
    embedding: data.embedding.values,
    message: message
  }
};

const results = $input.item.json.result || [];
const message = $('Generate Embedding').item.json.message;

// Build context from search results
const contexts = results.map(item => {
  const payload = item.payload;
  if (payload.type === 'faq') {
    return `Q: ${payload.question}\nA: ${payload.answer}`;
  } else {
    return `Page: ${payload.title} (${payload.route})\nDescription: ${payload.description}`;
  }
});

const context = contexts.join('\n\n---\n\n');

const systemPrompt = `You are HomeEase Assistant, a helpful chatbot for the HomeEase platform.

You help users with:
- Booking services and property rentals
- Account management and navigation
- Payment and cancellation policies
- General platform questions

Guidelines:
- Be friendly, concise, and professional
- Use the context below to answer accurately
- If a question is about navigation, mention the page route
- Keep responses under 150 words
- If unsure, suggest contacting support

--- CONTEXT FROM KNOWLEDGE BASE ---
${context}
--- END CONTEXT ---`;

const userPrompt = `User question: ${message}\n\nProvide a helpful answer based on the context above.`;

return {
  json: {
    systemPrompt,
    userPrompt,
    message
  }
};

const systemPrompt = $input.item.json.systemPrompt;
const userPrompt = $input.item.json.userPrompt;
const apiKey = process.env.GEMINI_API_KEY;

const response = await fetch(
  `https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash:generateContent?key=${apiKey}`,
  {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      contents: [
        {
          parts: [
            {
              text: systemPrompt + '\n\n' + userPrompt
            }
          ]
        }
      ],
      generationConfig: {
        temperature: 0.7,
        maxOutputTokens: 1024
      }
    })
  }
);

const data = await response.json();
const geminiResponse = data.candidates[0].content.parts[0].text;

return {
  json: {
    success: true,
    response: geminiResponse,
    timestamp: new Date().toISOString()
  }
};
